import React from 'react';

class Todolist extends React.Component {    
    constructor(props) {
        super(props);
        this.state = { completed: [], updateText: '' }; 
        this.handleUpdateClick = this.handleUpdateClick.bind(this);           
      }
    render() {
        return (
            <div>
                <ul>
                    {this.props.items.map(item => (
                    <li key={item.id}>
                    {item.text}
                    <button onClick={() => this.handleCompleteClick(item)}>Complete</button>
                    <button onClick={() => this.handleUpdateClick(item)}>Update</button>
                    <button onClick={() => this.handleDeleteClick(item)}>X</button>
                    </li>                
                    ))}
                </ul>
                <h5>Completed Task List : </h5>
                <ul>
                    {this.state.completed.map(complete => (
                    <li key={complete.id}>
                    {complete.text}                    
                    </li>                
                    ))}
                </ul>                
            </div>
        );
    }

    handleCompleteClick(item) {           
        const items = this.props.items;
        const index =  items.indexOf(item);
        if(index !== -1) {
            items.splice(index,1);
            this.setState( state => ({
                completed: state.completed.concat(item),            
                items: items,                                 
              })
              );
        }
    }

    handleDeleteClick(item) {           
        const items = this.props.items;
        const index =  items.indexOf(item);
        if(index !== -1) {
            items.splice(index,1);
            this.setState( {                          
                items: items,                                 
              }
            );
        }
    }

    handleUpdateClick(e, item) {
        const items = this.props.items;
        const index =  items.indexOf(item);
        items[index].text = this.state.updateText

        e.preventDefault();
        if (this.state.updateText.length === 0) {
        return;
        }    
        this.setState({
            items: items,
            updateText: ''            
        });
    }
}

export default Todolist;
