import React from 'react';

class Todolist extends React.Component {    
    constructor(props) {
        super(props);
        this.state = { completed: [], index: '' };            
      }
    render() {
        return (
            <div>
                <ul>
                    {this.props.items.map(item => (
                    <li key={item.id}>
                    {item.text}
                    <button onClick={() => this.handleCompleteClick(item)}>Complete</button>
                    <button>Update</button>
                    <button>X</button>
                    </li>                
                    ))}
                </ul>
                <h5>Completed Task List</h5>
                <ul>
                    {this.state.completed.map(complete => (
                    <li key={complete.id}>
                    {complete.text}                    
                    </li>                
                    ))}
                </ul>                
            </div>
        );
    }

    handleCompleteClick(item) {
        const itenerary= item;
        if(this.index !== -1)
        {
            this.setState( state => ({
            index: this.props.items.indexOf(itenerary.id),            
            completed: state.completed.concat(itenerary),            
            items: this.props.items.splice(this.index, 1),                                 
          })
          );
        }
        this.setState({
            index: '',
        });
        console.log(this.props.items);
    }
}

export default Todolist;
