import React from 'react';

class Todolist extends React.Component {    
    constructor(props) {
        super(props);
        this.state = { completed: [] };            
      }
    render() {
        return (
            <div>
                <ul>
                    {this.props.items.map(item => (
                    <li key={item.id}>
                    {item.text}
                    <button onClick={() => this.handleCompleteClick(item)}>Complete</button>
                    <button>Update</button>
                    <button>X</button>
                    </li>                
                    ))}
                </ul>
                <h5>Completed Task List</h5>
                <ul>
                    {this.state.completed.map(complete => (
                    <li key={complete.id}>
                    {complete.text}                    
                    </li>                
                    ))}
                </ul>                
            </div>
        );
    }

    handleCompleteClick(item) {
        const itenerary= item;
        console.log(itenerary);  
        console.log(this.props.items);
        const iindex = this.props.items.indexOf(itenerary.id);
        console.log(iindex);
        this.setState( state => ({
            completed: state.completed.concat(itenerary),
            items: this.props.items.splice(iindex, 1),                                 
          })
          );
        console.log(this.props.items);
    }
}

export default Todolist;
