import React from 'react';
import Modal from './Modal.js';

class Todolist extends React.Component {    
    constructor(props) {
        super(props);
        this.state = { completed: [], updateText: '', show: false };
        this.showModal = this.showModal.bind(this);
        this.hideModal = this.hideModal.bind(this);
        this.handleChange = this.handleChange.bind(this);        ;           
      }

    showModal = (item) => {        
        this.setState({ show: true });
        return item.text;
    };

    hideModal = () => {
        this.setState({ show: false });
    };

    render() {
        return (
            <div>
                <Modal show={this.state.show} handleClose={this.hideModal}>
                <p>Modal</p>
                <form onSubmit={this.handleUpdateClick}>
                    <input
                    id="new-todo"
                    onChange={this.handleChange}
                    value={this.state.updateText}
                    placeholder={this.showModal}
                    />
                    <button>
                    Update
                    </button>
                </form>
                </Modal>
                <ul>
                    {this.props.items.map(item => (
                    <li key={item.id}>
                    {item.text}
                    <button onClick={() => this.handleCompleteClick(item)}>Complete</button>
                    <button onClick={() => this.showModal(item)}>Update</button>
                    <button onClick={() => this.handleDeleteClick(item)}>X</button>
                    </li>                
                    ))}
                </ul>
                <h5>Completed Task List : </h5>
                <ul>
                    {this.state.completed.map(complete => (
                    <li key={complete.id}>
                    {complete.text}                    
                    </li>                
                    ))}
                </ul>                
            </div>
        );
    }

    handleCompleteClick(item) {           
        const items = this.props.items;
        const index =  items.indexOf(item);
        if(index !== -1) {
            items.splice(index,1);
            this.setState( state => ({
                completed: state.completed.concat(item),            
                items: items,                                 
              })
              );
        }
    }

    handleDeleteClick(item) {           
        const items = this.props.items;
        const index =  items.indexOf(item);
        if(index !== -1) {
            items.splice(index,1);
            this.setState( {                          
                items: items,                                 
              }
            );
        }
    }

    handleUpdateClick(e, item) {
        const items = this.props.items;
        const index =  items.indexOf(item);
        items[index].text = this.state.updateText

        e.preventDefault();
        if (this.state.updateText.length === 0) {
        return;
        }    
        this.setState({
            items: items,
            updateText: ''            
        });
    }

    handleChange(e) {
        this.setState({ updateText: e.target.value });
      }
}

export default Todolist;
